INSERT INTO `mg_transfer` (`id`, `name`, `position_id`, `transferBasis_id`, `tariff`, `itinerary`, `conditions`, `notes`, `vehicle_n_price`, `drivernumber`, `date_from`, `date_to`, `deathline`, `reservation`, `flag`, `date_created`, `date_updated`, `hide`) VALUES
(1, 'GATWICK AIRPORT (LGW)', 29, 49, '24 Hours  00:00 - 23:59', '<p><span class=\"metin\"><strong>Private Transfer:</strong><br />Private Transfer will be for your own personal use and will not be shared with other travelers. We recommend this transfer if you are looking to arrive and depart hassle free.<br /><br /><strong>Arrival Instructions:</strong><br />Please proceed to the Arrivals Hall after the baggage &amp; customs where our driver will be holding a clipboard with your name written on it. Please identify yourself by showing your confirmation voucher and in turn they will transfer you immediately.<br /><br /><strong>Departure Instructions:</strong><br />Wait at the lobby of your accommodation where our transfer assistance will meet you. Please present your service voucher and in turn they will transfer you immediately.</span></p>', '<p><span class=\"metin\">&bull; Please make sure that you entered travel information correctly at time of booking. Supplements might occur due to mis-information on arrival/departure details.<br />&bull; For this <strong>Private Transfer </strong>we advise you to call the Local Tour Operator from the numbers on the voucher&nbsp;<u>if you could not meet with the transfer assistance at remote airports or busy hours</u>&nbsp;maximum 20 minutes after your exact pick up time before leaving the Airport/Port/Residence. Otherwise your transfer may be counted as No Show of yours and refund may not apply.<br />&bull; Transfers to/from outside of city center is upon availability and may require supplement fee.<br />&bull; Delayed flight arrivals are monitored no extra charge for flight delays.<br />&bull; Amendments are upon availability and may require supplement fee.<br />&bull;&nbsp;Full cancellation applies in the event of No-Show. </span></p>\r\n<hr size=\"1\" />\r\n<p><span class=\"metin\">Tax:&nbsp;Included<br /></span></p>\r\n<hr size=\"1\" />\r\n<p><span class=\"metin\">Cancellation Policy: This service can be cancelled without any penalties&nbsp;<strong>4</strong>&nbsp;day(s) prior to first service date.</span></p>', '<p><span class=\"metin\"><strong>Maximum Waiting Time At Arrival Point:</strong><br />1 Hour<br />In the event of a problem with your luggage please notify local provider of the delay. There is no obligation to wait extra time. In case they can wait extra parking fee may be resulted due to delays must be paid on site.<br />&nbsp;<br /><strong>Maximum Waiting Time At Departure Point:</strong><br />20 Minutes<br />&nbsp; &nbsp;<br /><strong>Luggage Information:</strong><br />1 suitcase per person is allowed. On private transfers if the number of luggage are more than this or there are pushchairs/wheelchairs etc.; a big enough vehicle must be booked for comfort and safety. Oversized luggage (e.g. surfboards golf clubs or bikes) may have extra fee.&nbsp;Provider cannot be held responsible for damaged baggage personal belongings or any items left in the vehicle.<br /><br /><strong>Children/Infants:</strong><br />Please make sure infants and children are included in the total number of passengers e.g. if three adults one child and an infant are travelling a private transfer vehicle for at least five people needed to be booked. On shuttles infants under the age of 2 are free if they sit on a responsible adult`s knee but they must still be included in the booking. &nbsp;</span></p>', '1', '', '2017/01/01', '2017/12/01', 0, 0, 1, 1482878727, 1482878727, 1),
